INSERT INTO `gc_class_comment_datas` VALUES (9997556, '55700864', '语文', '老师讲课很棒，讲的知识点很细腻，也很实用，散文的阅读本来是一个难点，这下倒是不算什么了，老师讲得很好~', '5', '5', '4');
