INSERT INTO `gc_class_comment_datas` VALUES (9999148, '55700464', '理综', '我是听同学介绍找到老师的，说实话，他长得不帅，但课讲的真好，最吸引我的地方是他能把抽象的物理问题具象化，让人豁然开朗，一下找到了学习的窍门，老师，谢谢你！', '5', '5', '5');
