INSERT INTO `gc_class_comment_datas` VALUES (9999150, '55700466', '理综', '我妈妈说，如果没有老师的帮助，我连一般的普高可能都考不上！我现在取得这么大的进步，真的很感谢老师的辛勤付出，我一定好好努力，取得更好的中考成绩才能回报林老师。', '5', '5', '4');
INSERT INTO `gc_class_comment_datas` VALUES (9999151, '55700467', '理综', '老师教学负责认真，课堂活跃，致学严谨，细心，解题思路清楚，通俗易懂，我很喜欢上老师的课！', '5', '5', '4');
